import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, TextField, Button } from '@material-ui/core';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [error, setError] = useState(null); // Agregamos un estado para manejar errores

  useEffect(() => {
    axios.get('http://localhost:3001/tasks')
      .then(res => {
        setTasks(res.data);
      })
      .catch(err => console.error(err));
  }, []);

  const handleAddTask = () => {
    axios.post('http://localhost:3001/tasks/add', { task: newTask })
      .then(() => {
        setNewTask('');
        // Actualizar la lista de tareas después de agregar una nueva
        axios.get('http://localhost:3001/tasks')
          .then(res => {
            setTasks(res.data);
          })
          .catch(err => console.error(err)); // Agrega manejo de errores aquí
      })
      .catch(err => {
        setError(err.message); // Guardamos el mensaje de error en el estado
        console.error(err);
      });
  };

  return (
    <Container>
      <h1>Lista de Tareas</h1>
      <div>
        {tasks.map(task => (
          <div key={task.id}>
            <p>{task.task}</p>
          </div>
        ))}
      </div>
      <TextField
        label="Nueva Tarea"
        value={newTask}
        onChange={(e) => setNewTask(e.target.value)}
      />
      <Button variant="contained" color="primary" onClick={handleAddTask}>Agregar Tarea</Button>
      {error && <p>Error: {error}</p>} {/* Mostramos el mensaje de error si hay alguno */}
    </Container>
  );
}

export default App;
