import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Container, TextField, Button, Grid, Paper, Typography } from '@material-ui/core';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editingTask, setEditingTask] = useState(null);
  const [editedTaskText, setEditedTaskText] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = () => {
    axios.get('http://localhost:3002/tasks')
      .then(res => {
        setTasks(res.data);
      })
      .catch(err => {
        setError(err.message);
      });
  };

  const handleAddTask = () => {
    axios.post('http://localhost:3002/tasks/add', { task: newTask })
      .then(() => {
        setNewTask('');
        fetchTasks();
      })
      .catch(err => {
        setError(err.message);
      });
  };

  const handleEditTask = (taskId) => {
    setEditingTask(taskId);
    const taskToEdit = tasks.find(task => task.id === taskId);
    setEditedTaskText(taskToEdit.task);
  };

  const handleSaveEditedTask = () => {
    if (!editedTaskText.trim()) {
      setError('El texto de la tarea editada no puede estar vacío');
      return;
    }
    axios.put(`http://localhost:3002/tasks/update/${editingTask}`, { task: editedTaskText })
      .then(() => {
        setEditingTask(null);
        fetchTasks();
      })
      .catch(err => {
        setError(err.message);
      });
  };

  const handleCancelEdit = () => {
    setEditingTask(null);
    setEditedTaskText('');
  };

  const handleDeleteTask = (taskId) => {
    axios.delete(`http://localhost:3002/tasks/delete/${taskId}`)
      .then(() => {
        fetchTasks();
      })
      .catch(err => {
        setError(err.message);
      });
  };

  return (
    <Container>
      <Grid container spacing={2} alignItems="center">
        <Grid item xs={3}>
          <Typography variant="h5" gutterBottom>Nueva Tarea</Typography>
        </Grid>
        <Grid item xs={6}>
          <TextField
            label="Nueva Tarea"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            fullWidth
          />
        </Grid>
        <Grid item xs={3}>
          <Button variant="contained" color="primary" onClick={handleAddTask} fullWidth>Agregar Tarea</Button>
        </Grid>
      </Grid>

      <Typography variant="h4" gutterBottom style={{ textAlign: 'center', marginTop: '20px' }}>Lista de tareas</Typography>
      <Grid container spacing={2}>
        {tasks.map(task => (
          <Grid item xs={12} key={task.id}>
            <Paper elevation={3} style={{ padding: '10px' }}>
              {editingTask === task.id ? (
                <>
                  <TextField
                    value={editedTaskText}
                    onChange={(e) => setEditedTaskText(e.target.value)}
                  />
                  <Button variant="contained" color="primary" onClick={handleSaveEditedTask}>Guardar</Button>
                  <Button variant="contained" onClick={handleCancelEdit}>Cancelar</Button>
                </>
              ) : (
                <>
                  <p>{task.task}</p>
                  <Button variant="contained" onClick={() => handleEditTask(task.id)}>Editar</Button>
                  <Button variant="contained" color="secondary" onClick={() => handleDeleteTask(task.id)}>Eliminar</Button>
                </>
              )}
            </Paper>
          </Grid>
        ))}
      </Grid>
      {error && <p>Error: {error}</p>}
    </Container>
  );
}

export default App;
